import React, {useState} from 'react';
import {calculateWinner} from '../helpers';
import Board from './Board';

const styles={
    width: '200px',
    margin: '20px auto'
}

const Game = () => {
    const [board, setBoard] = useState(Array(9).fill({value: '', winner: false}));
    const [xIsNext, setXisNext] = useState(true);
    const winner= calculateWinner(board);


    const handleClick = i => {
        const boardCopy= [...board];
        if (boardCopy[i].value || winner) return;
        boardCopy[i]= xIsNext ? {value: 'X', winner: false} : {value: '0', winner: false};
        setBoard(boardCopy);
        setXisNext(!xIsNext);

    }

    const checkWinner = () => {
        if(winner) {
            board.forEach(element => element.winner = true);
        }
        
        return board;
    }

    const renderMoves = () => (
        <button onClick={() => setBoard(Array(9).fill(null))}>Start game</button>
    )
return (
    <>
    <Board squares={checkWinner} onClick={handleClick} />
    <div style={styles}>
        <p>{winner ? 'CONGRATULATIONS!!! Winner: '+ winner : 'Next player: ' + (xIsNext? 'X' : 'O')}</p>
        {renderMoves()};
    </div>
    </>
)
}

export default Game;
