import React from 'react';
// import {calculateWinner as winner} from '../helpers';

const style = {
    background: winner ? 'yellow' : 'lightblue',
    // background: 'lightblue',
    border: '2px solid darkblue',
    fontSize: '30px',
    fontWeight: '800',
    cursor: 'pointer',
    outline: 'none'
};

const Square = ({value, onClick}) => (
<button style={style}
//  [style.backgroundColor]="winner ? 'blue' :'yellow'" 
 onClick={onClick}>
    {value}
</button>
);

export default Square;
