import React from 'react';
// import ReactDOM from 'react-dom';
import {SingleInput} from './SingleInput'

class NameForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        };
      }
      myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
      }
      onClickk = () => {
          document.getElementById("formDetails").innerHTML=this.state.firstname+ this.state.lastname + this.state.email+this.state.subject+this.state.textarea;
      }
      render() {
        return (
          <form>
          <p>Enter your first name:</p>
          <input
            type='text'
            name="firstname"
            onChange={this.myChangeHandler}
          />
          <p>Enter your last name:</p>
            {/* <SingleInput name='lastname' onChange={this.myChangeHandler}></SingleInput> */}
          <input
            type='text'
            name='lastname'
            onChange={this.myChangeHandler}
          />
           <p>Enter your email:</p>
          <input
            type='email'
            name='email'
            onChange={this.myChangeHandler}
          />
           <p>Choose the subject:</p>
          <select name='subject' value={this.state.subject} onChange={this.myChangeHandler}>
        <option value="bug">Bug</option>
        <option value="question">Question</option>
        <option value="other">Other</option>
      </select>
      <p>Please write your message here:</p>
      <textarea name="textarea" value={this.state.textarea} onChange={this.myChangeHandler}/>
          <p>
          <button onClick={() => this.onClickk()}>Send</button></p>
          <p  id="formDetails"></p>
          </form>
        );
      }
  }
export default NameForm;
