import React from 'react';
// import ReactDOM from 'react-dom';
import {InputMic} from './input-mic'

class NameForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          username: '',
          age: null,
        //  mycar: 'Volvo'
        };
      }
      myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
      }
      onClickk = () => {
          document.getElementById("formDetails").innerHTML=this.state.lastname + '    ok    ' + this.state.email;
      }
      render() {
        return (
          <form>
          {/* <h1 >Hello {this.state.firstname} {this.state.lastname} {this.state.email}</h1> */}
          <p>Enter your first name:</p>
          {/* <InputMic name='lastname' onChange={this.myChangeHandler}></InputMic> */}
          <input
            type='text'
            value="firstname"
            onChange={this.myChangeHandler}
          />
          {/* <p>Enter your last name:</p> */}
          <input
            type='text'
            name='lastname'
            onChange={this.myChangeHandler}
          />
           <p>Enter your email:</p>
          <input
            type='email'
            name='email'
            onChange={this.myChangeHandler}
          />
          <select value={this.state.mycar} onChange={this.myChangeHandler}>
        <option value="Ford">Ford</option>
        <option value="Volvo">Volvo</option>
        <option value="Fiat">Fiat</option>
      </select>
          <p>
          <button onClick={() => this.onClickk()}>Send data!</button></p>
          <p  id="formDetails"></p>
          </form>
        );
      }
  }
export default NameForm;
